mapper.select:
select semester, selectcourse.studentId,student.name as studentName, selectcourse.staffId, selectcourse.courseId,course.name as courseName, testScore,score,ratio, finalScore, CASE WHEN finalScore>=90 THEN 4.0 WHEN finalScore>=85 THEN 3.7 WHEN finalScore>=82 THEN 3.3 WHEN finalScore>=78 THEN 3.0 WHEN finalScore>=75 THEN 2.7 WHEN finalScore>=72 THEN 2.3 WHEN finalScore>=68 THEN 2.0 WHEN finalScore>=64 THEN 1.5 WHEN finalScore>=60 THEN 1.0 ELSE 0 END as scorePoint From selectcourse,course,student
selectcourse.courseId = course.courseId and selectcourse.studentId = student.studentId and semester = #{semester}
and selectcourse.studentId = #{studentId}
and student.name = #{studentName}
and selectcourse.courseId = #{courseId}
and course.name = #{courseName}
limit #{pageCurrent},#{pageSize}

select semester, selectcourse.studentId,student.name as studentName, selectcourse.staffId, selectcourse.courseId,course.name as courseName,selectcourse.classtime, testScore,score,ratio, finalScore, CASE WHEN finalScore>=90 THEN 4.0 WHEN finalScore>=85 THEN 3.7 WHEN finalScore>=82 THEN 3.3 WHEN finalScore>=78 THEN 3.0 WHEN finalScore>=75 THEN 2.7 WHEN finalScore>=72 THEN 2.3 WHEN finalScore>=68 THEN 2.0 WHEN finalScore>=64 THEN 1.5 WHEN finalScore>=60 THEN 1.0 ELSE 0 END as scorePoint From selectcourse,course,student
selectcourse.courseId = course.courseId and selectcourse.studentId = student.studentId
limit #{pageCurrent},#{pageSize}

mapper.delete:
delete
from selectcourse

    selectcourse.semester = #{semester}
    AND selectcourse.studentId = #{studentId}
    AND selectcourse.staffId = #{staffId}
    AND selectcourse.courseId = #{courseId}
