mapper.select:
select selectcourse.semester, selectcourse.studentId,student.name as studentName, selectcourse.staffId,student.deptId as deptId,deptName, course.credit as courseCredit, selectcourse.courseId,course.name as courseName,selectcourse.classtime as classTime, status from selectcourse,course,student,dept,semestatus
selectcourse.courseId = course.courseId and selectcourse.studentId = student.studentId and student.deptId = dept.deptId and selectcourse.semester = semestatus.semester and selectcourse.semester = #{semester}
and selectcourse.studentId = #{studentId}

select opencourse.semester, course.name as courseName
From course,opencourse,teacher

    opencourse.courseId = course.courseId
    and opencourse.staffId = teacher.staffId

        and opencourse.semester = #{semester}


        and opencourse.courseId = #{courseId}


        and course.name = #{courseName}

select opencourse.semester,
opencourse.staffId,teachername as staffName,
course.credit as courseCredit,
opencourse.courseId,course.name as courseName,opencourse.classtime as classTime,
volume, remnant
From course,teacher,opencourse

    opencourse.courseId = course.courseId
    and opencourse.staffId = teacher.staffId
    and opencourse.semester = #{semester}

        and opencourse.courseId = #{courseId}


        and course.name = #{courseName}


limit #{pageCurrent},#{pageSize}

select score,testScore,finalScore,credit as courseCredit
from selectcourse,student,course

    selectcourse.courseId = course.courseId
    and selectcourse.studentId = student.studentId

        and selectcourse.semester = #{semester}


        and selectcourse.studentId = #{studentId}

select semester, selectcourse.studentId,student.name as studentName, selectcourse.staffId,student.deptId as deptId,deptName, course.credit as courseCredit, selectcourse.courseId,course.name as courseName,selectcourse.classtime as classTime, testScore,score,ratio, finalScore From selectcourse,course,student,dept
selectcourse.courseId = course.courseId and selectcourse.studentId = student.studentId and student.deptId = dept.deptId and semester = #{semester}
and selectcourse.studentId = #{studentId}
limit #{pageCurrent},#{pageSize}

select semester,
selectcourse.studentId,student.name as studentName,
selectcourse.staffId,student.deptId as deptId,deptName,
selectcourse.courseId,course.name as courseName,selectcourse.classtime as classTime,
testScore,score,ratio,
finalScore
From selectcourse,course,student,dept

    selectcourse.courseId = course.courseId
    and selectcourse.studentId = student.studentId
    and student.deptId = dept.deptId
    and semester = #{semester}

        and selectcourse.studentId = #{studentId}


        and student.name = #{studentName}


        and selectcourse.courseId = #{courseId}


        and course.name = #{courseName}


limit #{pageCurrent},#{pageSize}

select semester, selectcourse.studentId,student.name as studentName, selectcourse.staffId,student.deptId as deptId,deptName, selectcourse.courseId,course.name as courseName,selectcourse.classtime as classTime, testScore,score,ratio, finalScore From selectcourse,course,student,dept
selectcourse.courseId = course.courseId and selectcourse.studentId = student.studentId and student.deptId = dept.deptId
limit #{pageCurrent},#{pageSize}

mapper.delete:
delete
from selectcourse

    selectcourse.semester = #{semester}
    AND selectcourse.studentId = #{studentId}
    AND selectcourse.staffId = #{staffId}
    AND selectcourse.courseId = #{courseId}

mapper.select:
select semester,
       selectcourse.studentId,student.name as studentName,
       course.name as courseName,course.courseId,
       finalScore
from selectcourse,student,course

    selectcourse.studentId = student.studentId
    and selectcourse.courseId = course.courseId
    and selectcourse.semester = #{semester}

        and course.courseId = #{courseId}


        and course.name = #{courseName}

    ORDER BY finalScore DESC


    limit #{pageCurrent},#{pageSize}

select score,testScore,finalScore
from selectcourse,student,course

    selectcourse.courseId = course.courseId
    and selectcourse.studentId = student.studentId

        and selectcourse.semester = #{semester}


        and selectcourse.studentId = #{studentId}


        and student.name = #{studentName}


        and selectcourse.courseId = #{courseId}


        and course.name = #{courseName}

select semester,selectcourse.studentId,student.name as studentName, sum(course.credit * selectcourse.finalScore)/sum(course.credit) as semeFinalScore, deptName from selectcourse,course,student,dept
selectcourse.courseId = course.courseId and selectcourse.studentId = student.studentId and student.deptId = dept.deptId
and semester = #{semester}
and deptName = #{deptName}
GROUP BY selectcourse.studentId ORDER BY semeFinalScore DESC
limit #{pageCurrent},#{pageSize}

mapper.update:
Update selectcourse


        set score=#{S.score},testScore=#{S.testscore}


        set score=score+10



semester = #{semester}
and studentId = #{S.studentid} and courseId = #{S.courseid} and staffId = #{S.staffid} and classTime = #{S.classtime}
